import React from 'react';

import {
   GroupContainer,
   FormInputContainer,
   FormInputLabel
} from './form-input.styles';

const FormInput = ({ handleChange, label, ...props}) => (
   <div className='group'>
      <input className='form-input' onChange={handleChange} {...otherProps}/>
      {label ? (
         <label 
            className={`${
               otherProps.value.length ? 'shrink' : ''
               } form-input-label`}
         >
            {label}
         </label>)
         : null
      }
   </div>
);

export default FormInput;
